import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as l,f as d}from"./app-e4c3d7ff.js";const a={},r=d('<h2 id="成语积累" tabindex="-1"><a class="header-anchor" href="#成语积累" aria-hidden="true">#</a> 成语积累</h2><table><thead><tr><th style="text-align:left;">成语</th><th style="text-align:left;">解释</th></tr></thead><tbody><tr><td style="text-align:left;">方兴未艾</td><td style="text-align:left;">通常用来形容某种事物正在蓬勃发展、兴盛壮大，但尚未达到巅峰状态。这个成语强调事物的前景看好，但也意味着还有进一步的潜力和发展空间。</td></tr><tr><td style="text-align:left;">曲意逢迎</td><td style="text-align:left;">指的是为了讨好或取悦他人而刻意迎合对方的意愿、喜好或要求，通常是出于虚伪或不真诚的目的。这个短语有时也用来形容某人说话或行为虚伪、阿谀奉承，往往是出于个人私利或权谋。</td></tr><tr><td style="text-align:left;">马放南山</td><td style="text-align:left;">意思是比喻天下太平，不再用兵。现形容思想麻痹。</td></tr><tr><td style="text-align:left;">釜底抽薪</td><td style="text-align:left;">意思是从锅底抽出柴火，比喻从事物的根本部分着手，解决问题的根本办法。这个成语常常用来形容解决问题的方法要迅猛、果断，不拖泥带水，直击问题的核心，以达到事半功倍的效果。</td></tr><tr><td style="text-align:left;">抱薪救火</td><td style="text-align:left;">意思是在火灾中抱着柴薪去扑灭火焰，比喻用错误的或不恰当的方法去解决问题，反而加重问题的严重性</td></tr><tr><td style="text-align:left;">目无全牛</td><td style="text-align:left;">意思是眼中没有完整的牛，只有牛的筋骨结构；形容人的技艺高超，得心应手，已经到达非常纯熟的地步</td></tr><tr><td style="text-align:left;">祸起萧墙</td><td style="text-align:left;">“祸起萧墙”在句中可充当谓语、宾语、定语；含贬义，指祸乱发生在家里。比喻内部发生祸乱。</td></tr><tr><td style="text-align:left;">洞如观火</td><td style="text-align:left;">意思是形容观察事物非常清楚，好像看火一样</td></tr><tr><td style="text-align:left;">以一持万</td><td style="text-align:left;">意思是形容抓住关键，可以控制全局</td></tr></tbody></table>',2),i=[r];function n(s,f){return e(),l("div",null,i)}const y=t(a,[["render",n],["__file","idiom.html.vue"]]);export{y as default};
