const n=JSON.parse('{"key":"v-7613fbd6","path":"/zh/posts/other/nginx_ssl.html","title":"nginx_SSL证书部署","lang":"zh-CN","frontmatter":{"title":"nginx_SSL证书部署","date":"2024-11-25T00:00:00.000Z","icon":"tag","category":["nginx"],"tag":["SSL"],"description":"轻量级服务器部署SSL证书 将下载好的ssl证书压缩包上传的nginx目录下conf.d，解压 编辑 default.conf server { #SSL 默认访问端口号为 443 listen 443 ssl; #请填写绑定证书的域名 server_name liuyuedeyu.top; #请填写证书文件的相对路径或绝对路径 ssl_certificate conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top_bundle.crt; #请填写私钥文件的相对路径或绝对路径 ssl_certificate_key conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top.key; ssl_session_timeout 5m; #请按照以下协议配置 ssl_protocols TLSv1.2 TLSv1.3; #请按照以下套件配置，配置加密套件，写法遵循 openssl 标准。 ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:HIGH:!aNULL:!MD5:!RC4:!DHE; ssl_prefer_server_ciphers on; location / { root /usr/share/nginx/html; index index.html index.htm; } error_page 500 502 503 504 /50x.html; location = /50x.html { root /usr/share/nginx/html; } } server { listen 80; listen [::]:80; #请填写绑定证书的域名 server_name liuyuedeyu.top; #把http的域名请求转成https return 301 https://$host$request_uri; }","head":[["link",{"rel":"alternate","hreflang":"en-us","href":"https://github.com/Lzx-xb/lzx-xb.github.io/posts/other/nginx_ssl.html"}],["meta",{"property":"og:url","content":"https://github.com/Lzx-xb/lzx-xb.github.io/zh/posts/other/nginx_ssl.html"}],["meta",{"property":"og:site_name","content":"漫步人生路"}],["meta",{"property":"og:title","content":"nginx_SSL证书部署"}],["meta",{"property":"og:description","content":"轻量级服务器部署SSL证书 将下载好的ssl证书压缩包上传的nginx目录下conf.d，解压 编辑 default.conf server { #SSL 默认访问端口号为 443 listen 443 ssl; #请填写绑定证书的域名 server_name liuyuedeyu.top; #请填写证书文件的相对路径或绝对路径 ssl_certificate conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top_bundle.crt; #请填写私钥文件的相对路径或绝对路径 ssl_certificate_key conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top.key; ssl_session_timeout 5m; #请按照以下协议配置 ssl_protocols TLSv1.2 TLSv1.3; #请按照以下套件配置，配置加密套件，写法遵循 openssl 标准。 ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:HIGH:!aNULL:!MD5:!RC4:!DHE; ssl_prefer_server_ciphers on; location / { root /usr/share/nginx/html; index index.html index.htm; } error_page 500 502 503 504 /50x.html; location = /50x.html { root /usr/share/nginx/html; } } server { listen 80; listen [::]:80; #请填写绑定证书的域名 server_name liuyuedeyu.top; #把http的域名请求转成https return 301 https://$host$request_uri; }"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:locale:alternate","content":"en-US"}],["meta",{"property":"og:updated_time","content":"2024-11-25T05:10:17.000Z"}],["meta",{"property":"article:author","content":"漫步人生路"}],["meta",{"property":"article:tag","content":"SSL"}],["meta",{"property":"article:published_time","content":"2024-11-25T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2024-11-25T05:10:17.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"nginx_SSL证书部署\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2024-11-25T00:00:00.000Z\\",\\"dateModified\\":\\"2024-11-25T05:10:17.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"漫步人生路\\",\\"url\\":\\"https://liuyuedeyu.top\\"}]}"]]},"headers":[],"git":{"createdTime":1732511417000,"updatedTime":1732511417000,"contributors":[{"name":"lzx-xb","email":"321337450@qq.com","commits":1}]},"readingTime":{"minutes":0.79,"words":236},"filePathRelative":"zh/posts/other/nginx_ssl.md","localizedDate":"2024年11月25日","excerpt":"<h1> 轻量级服务器部署SSL证书</h1>\\n<ol>\\n<li>将下载好的ssl证书压缩包上传的nginx目录下conf.d，解压</li>\\n<li>编辑 default.conf</li>\\n</ol>\\n<div class=\\"language-bash line-numbers-mode\\" data-ext=\\"sh\\"><pre class=\\"language-bash\\"><code>server <span class=\\"token punctuation\\">{</span>\\n     <span class=\\"token comment\\">#SSL 默认访问端口号为 443</span>\\n     listen <span class=\\"token number\\">443</span> ssl<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token comment\\">#请填写绑定证书的域名</span>\\n     server_name liuyuedeyu.top<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token comment\\">#请填写证书文件的相对路径或绝对路径</span>\\n     ssl_certificate conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top_bundle.crt<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token comment\\">#请填写私钥文件的相对路径或绝对路径</span>\\n     ssl_certificate_key conf.d/liuyuedeyu.top_nginx/liuyuedeyu.top.key<span class=\\"token punctuation\\">;</span>\\n     ssl_session_timeout 5m<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token comment\\">#请按照以下协议配置</span>\\n     ssl_protocols TLSv1.2 TLSv1.3<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token comment\\">#请按照以下套件配置，配置加密套件，写法遵循 openssl 标准。</span>\\n     ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:HIGH:<span class=\\"token operator\\">!</span>aNULL:<span class=\\"token operator\\">!</span>MD5:<span class=\\"token operator\\">!</span>RC4:<span class=\\"token operator\\">!</span>DHE<span class=\\"token punctuation\\">;</span>\\n     ssl_prefer_server_ciphers on<span class=\\"token punctuation\\">;</span>\\n     location / <span class=\\"token punctuation\\">{</span>\\n        root /usr/share/nginx/html<span class=\\"token punctuation\\">;</span>\\n        index index.html index.htm<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token punctuation\\">}</span>\\n     error_page <span class=\\"token number\\">500</span> <span class=\\"token number\\">502</span> <span class=\\"token number\\">503</span> <span class=\\"token number\\">504</span> /50x.html<span class=\\"token punctuation\\">;</span>\\n     location <span class=\\"token operator\\">=</span> /50x.html <span class=\\"token punctuation\\">{</span>\\n        root /usr/share/nginx/html<span class=\\"token punctuation\\">;</span>\\n     <span class=\\"token punctuation\\">}</span>\\n <span class=\\"token punctuation\\">}</span>\\nserver <span class=\\"token punctuation\\">{</span>\\n listen <span class=\\"token number\\">80</span><span class=\\"token punctuation\\">;</span>\\n listen <span class=\\"token punctuation\\">[</span>::<span class=\\"token punctuation\\">]</span>:80<span class=\\"token punctuation\\">;</span>\\n <span class=\\"token comment\\">#请填写绑定证书的域名</span>\\n server_name liuyuedeyu.top<span class=\\"token punctuation\\">;</span> \\n <span class=\\"token comment\\">#把http的域名请求转成https</span>\\n <span class=\\"token builtin class-name\\">return</span> <span class=\\"token number\\">301</span> https://<span class=\\"token variable\\">$host</span><span class=\\"token variable\\">$request_uri</span><span class=\\"token punctuation\\">;</span> \\n<span class=\\"token punctuation\\">}</span>\\n</code></pre><div class=\\"line-numbers\\" aria-hidden=\\"true\\"><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div></div></div>","autoDesc":true}');export{n as data};
